//SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract AccessControl {

    event RoleGranted(
        bytes32  indexed role,
        address indexed account
    );

    event RoleRevoked(
        bytes32 indexed role,
        address indexed account
    );

    mapping(bytes32 => mapping(address => bool)) public roles;

    /*
        these were declared as private to save gas
    */
    bytes32 private constant ADMIN = keccak256(abi.encodePacked("ADMIN"));
    bytes32 private constant USER = keccak256(abi.encodePacked("USER"));

    modifier OnlyRole(bytes32 _role) {
        require(roles[_role][msg.sender], "Not Authorized");
        _;
    }

    /*
        we set up the first admin
    */
    constructor(){
        _grantRole(ADMIN, msg.sender);
    }

    /*
        internal version of the grantRole function
    */
    function _grantRole(bytes32 _role, address _account) internal {
        roles[_role][_account] = true;
        emit RoleGranted(_role, _account);
    }

    /*
        external version of the grantRole function
    */
    function grantRole(bytes32 _role, address _account) external OnlyRole(ADMIN) {
        _grantRole(_role, _account);
    }

    /*
        revoking roles function
    */
    function revokeRole(bytes32 _role, address _account) external OnlyRole(ADMIN) {
        roles[_role][_account] = false;
        emit RoleRevoked(_role, _account);
    }

}
