//SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract ArrayShift {
    uint[] public arr;

    function example() public {
        arr = [1, 2, 3];
        delete arr[1]; // [1, 0, 3] we dont want this
    }

    // [1, 2, 3] --> remove(1) --> [1, 3, 3] --> [1, 3]
    // [1, 2, 3, 4, 5, 6] --> remove(2) --> [1, 2, 4, 5, 6, 6] --> [1, 2, 4, 5, 6]
    function remove(uint _index) public {
        require(_index < arr.length, "index out of bound");

        for (uint i = _index; i < arr.length - 1; i++) { //shift the elements in the array (moves all elements 
            arr[i] = arr[i + 1];                         //after the index that we want to delete to the left)
        }
        arr.pop();                                       //deletes the last position in the array
    }
}
